/*ಠ_ಠ*/"use strict"
var styles = require("ansi-styles")
styles.line = { open: "\n", close: "" }
styles.tab = { open: "\t", close: "" }
styles._ = styles.space = { open: " ", close: " "}

module.exports = (function clor (s) {
  $._string = s || ""

  function $ (s) {
    return clor($._string + (s || "") + styles.reset.close)
  }

  $.log = function (obj) {
    (obj || console.log).apply($._string, [].slice.call(arguments, 1))
  }

  $.toString = function () {
    return $._string + styles.reset.close
  }

  Object.defineProperty($, "string", {
    get: function () {
      return $.toString()
    }
  })

  Object.keys(styles).forEach(function (style) {
    Object.defineProperty($, style, {
      get: function () {
        return clor($._string + styles[style].open)
      }
    })
  })

  return $
}())
